import 'package:flutter/material.dart';

import 'product_widget.dart';

class ProductScreen extends StatefulWidget {
  const ProductScreen({super.key});

  @override
  State<ProductScreen> createState() => _ProductScreenState();
}

class _ProductScreenState extends State<ProductScreen> {

  List productCate = ["Shoes", "Polo Shirt", "Watches", "Perfumes", "Caps", "Glasses", "Scarfs"];
  List productName = ["Nike", "T-Shirt", "Rolex", "Creed", "Addidas", "Rayban", "Abc"];
  List productAvail = ["Sold", "Available", "Sold", "Available", "Available", "Available", "Sold"];
  List productPrice = ["12.45", "29.30", "50.00", "188.00", "5.25", "2", "1.55"];
  List productPic = ["data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,"];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text("Products List",style: TextStyle(color: Colors.white),),
        centerTitle: true,
        elevation: 10,
        backgroundColor: Colors.black,
      ),
      body: ListView.builder(
        itemCount: productName.length, // 7
        itemBuilder: (context, index) {
        return  ProductWidget(
          productImage: "${productPic[index]}",
          productName: "${productName[index]}",
          productCate: "${productCate[index]}",
          productPrice: "${productPrice[index]}",
          productAvail: "${productAvail[index]}",
        );
      },)
    );
  }
}

